function disp(d){d.classList.contains("hidden","visuallyhidden")?(d.classList.remove("hidden"),setTimeout(function(){d.classList.remove("visuallyhidden")},250)):(d.classList.add("visuallyhidden"),setTimeout(function(){d.classList.add("hidden")},350))}window.onload=function(){modal=document.getElementById("modal"),modal.classList.add("visuallyhidden","hidden")};